targets.queryText:
SELECT
(s.timestep + ${timezone_offset})*3600 AS timestamp,
s.value/b.value AS basic,
s.value/o.value AS optimized
FROM ramping_cost_function s
LEFT JOIN environment e ON e.id = s.environment_id
LEFT JOIN (
SELECT timestep, value FROM   ramping_cost_function WHERE environment_id = ${basic_rbc_environment_id}
) b ON b.timestep = s.timestep
LEFT JOIN (
SELECT timestep, value FROM   ramping_cost_function WHERE environment_id = ${optimized_rbc_environment_id}
) o ON o.timestep = s.timestep
WHERE e.simulation_id = ${simulation_id}

